import styled from 'styled-components';

export const InputContainer = styled.div `

width: auto;
height: auto;
align-items: center;
justify-content: flex-end;
font-size: 24px;
display:flex;


    input {
    width: 100%;
    height: 110px;
    background-color: #121212;
    border-radius: 20px;
    border: 0;
    text-align:end;
    padding: 0 0.5rem ;
    color: #FAFAFA;
    letter-spacing: 1px;


}

`
