import styled from 'styled-components'

export const ButtonContainer = styled.button `


border: 1px solid #ccc;
padding: 0.5rem;
border-radius: 4rem;
width: 80px;
height: 80px;
margin: 0.5rem;
align-items: center;
border-radius: 28px;
display: flex;
color: #fafafa;
justify-content: center;
align-items: center;

border: 1px solid rgba(0, 0, 0, 0.00);
background: #161616;
box-shadow: 0px 4px 4px 0px rgba(0, 0, 0, 0.25);



`
