import Input from "./components/Input";
import Button from "./components/Button";
import { Container, Content, Row, Column } from "./styles";
import colors from "./colors";
import { useState } from "react";

const App = () => {
  const [currentNumber, setCurrentNumber] = useState("0");
  const [previousNumber, setPreviousNumber] = useState("0");

  const handleOnClear = () => {
    setCurrentNumber("0");
    setPreviousNumber("0");
  };

  // ${prev === "0" ? "" : prev}
  const handleAddNumber = (number) => {
    setCurrentNumber((prev) => {
      console.log("prev = " + setCurrentNumber.prev);
      return `${prev}${number}`;
    });
  };

  const handleSumNumber = () => {
    if (previousNumber === "0") {
      setPreviousNumber(String(currentNumber));
      setCurrentNumber("0");

      // console.log('previousNumber = ' + previousNumber, 'currentNumber = ' + currentNumber)
    } else {
      // console.log('previousNumber = ' + previousNumber, 'currentNumber = ' + currentNumber)
      const sum = Number(previousNumber) + Number(currentNumber);
      setCurrentNumber(String(sum));
    }
  };

  return (
    <Container>
      <Content>
        <Input value={currentNumber} />

        <Row>
          <Button label="C" color={colors.red} onClick={handleOnClear} />
          <Button
            label="&#x00F7;"
            color={colors.primary}
            onClick={() => handleAddNumber("")}
          />
        </Row>
        <Row>
          <Button label="7" onClick={() => handleAddNumber("7")} />
          <Button label="8" onClick={() => handleAddNumber("8")} />
          <Button label="9" onClick={() => handleAddNumber("9")} />
          <Button
            label="&#x00D7;"
            color={colors.primary}
            onClick={() => handleAddNumber("6")}
          />
        </Row>
        <Row>
          <Button label="4" onClick={() => handleAddNumber("4")} />
          <Button label="5" onClick={() => handleAddNumber("5")} />
          <Button label="6" onClick={() => handleAddNumber("6")} />
          <Button
            label="&#x2212;"
            color={colors.primary}
            onClick={() => handleAddNumber("")}
          />
        </Row>
        <Row>
          <Button label="1" onClick={() => handleAddNumber("1")} />
          <Button label="2" onClick={() => handleAddNumber("2")} />
          <Button label="3" onClick={() => handleAddNumber("3")} />
          <Button
            label="&#x002B;"
            color={colors.primary}
            onClick={handleSumNumber}
          />
        </Row>

        <Row>
          <Button label="&#x2190;" color={colors.primary} />
          <Button label="0" onClick={() => handleAddNumber("0")} />
          <Button label="," onClick={() => handleAddNumber(".")} />
          <Button label="&#x003D;" bgSpecial={colors.primaryGradient} />
        </Row>
      </Content>
    </Container>
  );
};

export default App;
