import Input from "./components/Input";
import Button from "./components/Button";
import { Container, Content, Row, Column } from "./styles";
import colors from './colors';
import {useState} from 'react';

const App = () => {
  const [currentNumber, setCurrentNumber] = useState('0');
  const handleAddNumber = (number) => {
    setCurrentNumber(prev => `${number}${prev}`)
  }
  return (
    <Container>
      <Content>
        <Input value={currentNumber} />

        <Row>


        <Button label="C" color={colors.red}/>
          <Button label="&#x00F7;" color={colors.primary}  />

        </Row>
        <Row>

        <Button label="7" />
          <Button label="8" />
          <Button label="9" />
          <Button label="&#x00D7;"  color={colors.primary}  />
        </Row>
        <Row>
        <Button label="4" />
          <Button label="5" />
          <Button label="6" />
          <Button label="&#x2212;"  color={colors.primary} />
        </Row>
        <Row>
        <Button label="1" />
          <Button label="2" />
          <Button label="3" />
          <Button label="&#x002B;" color={colors.primary}/>
        </Row>

        <Row>
        <Button label="&#x2190;" color={colors.primary} />
          <Button label="0" />
          <Button label="," />
          <Button label="&#x003D;"  bgSpecial={colors.primaryGradient} />
        </Row>
      </Content>
    </Container>
  );
};

export default App;
