import styled from "styled-components";

export const Container = styled.div `
width: 100%;
height: 100vh;
background: radial-gradient(45.57% 82.82% at 50% 17.19%, #2E3240 0%, #202433 100%);
display: flex;
align-items:center;
justify-content: center;

`
export const Content = styled.div `
    background-color: #121212;
    width: 80%;
    max-width: 388px;
    min-height: 350px;
    border-radius: 36px;
    border: 1px rgba(0, 0, 0, 0.50);
    padding: 1rem;




`
