import styled from 'styled-components'

export const ButtonContainer = styled.button `


font-size: 40px;
padding: 12px;
width:100%;

margin: 6px;
align-items: center;
border-radius: 28px;

color: #fafafa;
justify-content: center;
align-items: center;

border: 1px solid rgba(0, 0, 0, 0.00);
background: #161616;
box-shadow: 0px 4px 4px 0px rgba(0, 0, 0, 0.25);

flex: 1;

  &:hover {
    background-color: #0056b3;


`
