import styled from 'styled-components';

export const InputContainer = styled.div `

width: 100%;
height: 75px;
background-color: #AAFFAA;
align-items: center;
justify-content: flex-end;
font-size: 24px;
 

    input {
    width: 100%;
    height: 75px;
    background-color: #AAFFAA;
    border: 0;
    text-align:end;


}

`
