import styled from 'styled-components';

export const InputContainer = styled.div `

width: auto;
height: auto;
background-color: #ddd077;
align-items: center;
justify-content: flex-end;
font-size: 24px;
    padding: 10px;

    input {
    width: 100%;
    height: 75px;
    background-color: #AAFFAA;
    border: 0;
    text-align:end;


}

`
