import styled from 'styled-components';

export const InputContainer = styled.div `

width: auto;
height: auto;
align-items: center;
justify-content: flex-end;
font-size: 24px;
  padding: 0.5rem;

    input {
    width: 100%;
    height: 75px;
    background-color: #ddd077;
    border-radius: 20px;
    border: 0;
    text-align:end;
      padding: 0 0.5rem ;
  


}

`
